export function nameToIcon(name: string) {
  switch (name) {
    case 'cat':
      return <Icons.Cat />;
    case 'dog':
      return <Icons.Dog />;
    case 'fish':
      return <Icons.Fish />;
    case 'rabbit':
      return <Icons.Rabbit />;
    case 'snail':
      return <Icons.Snail />;
    case 'turtle':
      return <Icons.Turtle />;
    case 'squirrel':
      return <Icons.Squirrel />;
    case 'bird':
      return <Icons.Bird />;
    case 'bug':
      return <Icons.Bug />;
    case 'rat':
      return <Icons.Rat />;
    case 'egg':
      return <Icons.Egg />;
    default:
      return <Icons.logo />;
  }
}
